	private static List<Map<String, Object>> extractMsg(String str) {
		Document document = Jsoup.parseBodyFragment(str);// Â∞ÜÊäìÂèñÂõûÊù•ÁöÑÊåáÂÆöÂÜÖÂÆπÂà©Áî®jsoupËΩ¨Êç¢‰∏∫ÂØπË±?
		Element body = document.body();
		// Elements tables = body.select("table");//Êü•ÊâæÊ†∑Âºè‰∏∫xxÁöÑÂÖÉÁ¥†ÈõÜÂê?
		// ‰ΩøÁî®ÈÄâÊã©Âô®È?âÊã©ËØ•tableÂÜÖÊâÄÊúâÁöÑ<tr> <tr/>
		Elements trs = body.select("tr");
		List<String> titleList = new ArrayList<>();
		List<Map<String, Object>> retList = new ArrayList<>();
		// ÈÅçÂéÜËØ•Ë°®Ê†ºÂÜÖÁöÑÊâÄÊúâÁöÑ<tr> <tr/>
		for (int i = 0; i < trs.size(); ++i) {
			// Ëé∑Âèñ‰∏?‰∏™tr
			Element tr = trs.get(i);
			if (i == 0) {
				// Ëé∑ÂèñËØ•Ë°åÁöÑÊâÄÊúâtdËäÇÁÇπ
				Elements ths = tr.select("th");
				// ÈÄâÊã©Êüê‰∏Ä‰∏™thËäÇÁÇπ
				for (int j = 0; j < ths.size(); ++j) {
					Element th = ths.get(j);
					// Ëé∑ÂèñÊñáÊú¨‰ø°ÊÅØ ‰Ωú‰∏∫key
					titleList.add(th.text());
				}
			} else {
				// Ëé∑ÂèñËØ•Ë°åÁöÑÊâÄÊúâtdËäÇÁÇπ
				Elements tds = tr.select("td");
				// ÈÄâÊã©Êüê‰∏Ä‰∏™tdËäÇÁÇπ
				Map<String, Object> tdMap = new HashMap<>();
				for (int j = 0; j < tds.size(); ++j) {
					Element td = tds.get(j);
					// Ëé∑ÂèñtdËäÇÁÇπÁöÑÊâÄÊúâdiv
					Elements as = td.select("a");
					// ÈÄâÊã©‰∏?‰∏™div
					for (int k = 0; k < as.size(); k++) {
						Element a = as.get(k);
						String href = a.attr("href");
						// Ëé∑ÂèñÊñáÊú¨‰ø°ÊÅØ
						String text = a.text();
						tdMap.put(titleList.get(j), text);
						tdMap.put("href", href);
					}
					if (as.size() == 0) {
						// Ëé∑ÂèñÊñáÊú¨‰ø°ÊÅØ
						tdMap.put(titleList.get(j), td.text());
					}
				}
				retList.add(tdMap);
			}
		}
		return retList;
	}
	
	private static String getAjaxByPhontomjs(String url) throws IOException {
		long time = System.currentTimeMillis();
		String content = getAjaxCotnent(url);
		System.out.println(content);
		System.out.println("ÂàóË°®ÊäìÂèñËÄóÊó∂Ôº?" + (System.currentTimeMillis() - time));
		// System.out.println(content);
		try {
			Document document = Jsoup.parseBodyFragment(content);
			Element body = document.body();
			Elements xyxf_box = body.getElementsByClass("xydm_box");
			// System.out.println(xyxf_box.html());
			List<Map<String, Object>> list = extractMsg(xyxf_box.html());
			if (list.size() > 0) {
				String host = url.substring(0, url.lastIndexOf("/") + 1);
				int i = 0;
				for (Map<String, Object> map : list) {
					if (map.containsKey("href")) {
						map.put("href", host + map.get("href"));
						int a = i + 1;
						System.out.println("Âº?ÂßãÊäìÂèñÊòéÁª?(" + a + "):" + map.get("href"));
						long time1 = System.currentTimeMillis();
						String result = CrawlerPhontomjsByFZXY.getAjaxByPhontomjs((String)map.get("href"));
						System.out.println("ÊäìÂèñÊòéÁªÜÁªìÊûú(" + a + "):" + result);
						System.out.println("ÊòéÁªÜÊäìÂèñËÄóÊó∂(" + a + ")Ôº?" + (System.currentTimeMillis() - time1));
						map.put("detail", result);
					}
					i++;
				}
			}
			return JSON.toJSONString(list);
		} catch (Exception e) {
			e.printStackTrace();
			return "error";
		}
	}
	
	private static String getAjaxCotnent(String url) throws IOException {
		Runtime rt = Runtime.getRuntime();
		Process p = rt.exec(phontomjs_exe + " " + phontomjs_js + " " + url + " list");// ËøôÈáåÊàëÁöÑcodes.jsÊòØ‰øùÂ≠òÂú®cÁõò‰∏ãÈù¢ÁöÑphantomjsÁõÆÂΩï
		InputStream is = p.getInputStream();
		BufferedReader br = new BufferedReader(new InputStreamReader(is));
		StringBuffer sbf = new StringBuffer();
		String tmp = "";
		while ((tmp = br.readLine()) != null) {
			sbf.append(tmp);
		}
		// System.out.println("1:"+sbf.toString());
		return sbf.toString();
	}
	
